document.addEventListener("turbolinks:load", function () {
  $("body").on('click', '.btn.grid-item-save-story', function (event) {
    event.preventDefault();
    let id = this.id;
    let linksDiv = $("links-div-" + id);
    let saveStoryWrapper = $("#grid-item-save-story-wrapper-" + id)

    $.ajax({
      url: '/stories/' + id + '/save_story',
      method: 'post',
    })
      .done(function (data) {
        if (data['success']) {
          // remove link
          saveStoryWrapper.remove();
          // append text and forget btn
          linksDiv.append("<br><p class='status-text-"+ id +"'>"+ data['message'] +"</p>");
          linksDiv.append("<p class='story-actions-links grid-item-save-story-wrapper-" + id + "'><a href='javascript: void(0)' id="+ id +" class='btn btn-sm btn-outline-dark forget_story_link'><i class='fas fa-times mr-1'></i> Forget Story</a></p>");
        } else {
          linksDiv.append("<br><p class='status-text-" + id + "'>" + data['message'] + "</p>");
        };
      })
  })
});
