document.addEventListener("turbolinks:load", function () {
  $("body").on('click', '.btn.grid-item-save-story', function (event) {
    event.preventDefault();
    let id = this.id;
    console.log("id", id)

    $.ajax({
      url: '/stories/' + id + '/save_story',
      method: 'post',
    })
      .done(function (data) {
        console.log(data)
        if (data['success']) {
          $('tr#' + id).css("background-color", "#98FB98");
          $('tr#' + id + ' .publish-btn').remove();
          $('#update-td-' + id).append("<br><p class='status-text-" + id + "'>Approved! Showing on " + data['show_date'] + "</p>")
        } else {
          $('tr#' + id).remove();
        };
      })
  })
});
