document.addEventListener("turbolinks:load", function () {
  $("#menu-toggle").on('click', function (e) {
    e.preventDefault();
    $("#wrapper").toggleClass("sb-closed");
  });


  $('body').on('click', function (e) {
    let clickedPageContent = $(e.target).is('#page-content-wrapper');
    let clickedGridItem = $(e.target).closest('.grid-item').length === 0;
    let widthIsCorrect = $(window).width() < 999;
    let sidebarIsOpen = !$("#wrapper").hasClass("sb-closed");
    console.log('clicked')
    console.log('clicked page content', clickedPageContent)
    console.log('clicked grid item', clickedGridItem)
    if (clickedPageContent || clickedGridItem) {
      console.log('clicked the right thing')
      if (widthIsCorrect && sidebarIsOpen) {
        console.log('clicked the right thing with the right settings')
        // close the menu
        $("#wrapper").addClass("sb-closed");
      }
    }
  });

