document.addEventListener("turbolinks:load", function () {
  $("#menu-toggle").on('click', function (e) {
    e.preventDefault();
    $("#wrapper").toggleClass("sb-closed");
  });

  $(document).bind('click', function (e) {
    if (!$(e.target).is('#special')) {
      // do something
    }
  });

  $("body").on('click', function(e) {
    let clickedItem = $(e.target);
    let screenWidth = $(window).width();
    let sidebarClosed = $("#wrapper").hasClass("sb-closed");
    let didNotClickNav = (!clickedItem.is(".navbar") || !clickedItem.is(""));
    console.log('here');

    if (screenWidth < 999 && !sidebarClosed && didNotClickNav) {
      console.log('really here');
      // close the menu
      $("#wrapper").addClass("sb-closed");
    }
  })
})
